import {
  Button,
  Container,
  Grid,
  TextField,
  Typography,
} from "@material-ui/core";
import React, { useEffect, useState } from "react";
import { obtenerUsuarioActual } from "../../actions/UsuarioAction";
import style from "../Tool/Style";

const PerfilUsuario = () => {

const [usuario, setUsuario]= useState({
  //Le indicamos q estos son los valores q quiero mapear
   NombreCompleto: '',
   Email:'',
   Password:'',
   ConfirmarPassword:'',
})

 //Creamos la funcion q va disparar los valores al virtual DOM de REACT-- "e" = al contenido d la caja d texto
 const ingresarValoresMemoria = (e) => {
  const { name, value } = e.target;

  //Si quiero cambiar los valores d mi var d estado 'usuario' llamamos al metodo 'setUsuario'
  //Abrimos una f dentro d 'setUsuario' para q mantenga todo lo q existia antes y solo modifiq el valor 'NombreCompleto' d la c. d texto
  // A travs d su propiedad name y value.. 'value' es lo q ingresa el cliente en el formular y asi sucesivament.... con todo los 'name' d las cajas
  setUsuario((anterior) => ({
    ...anterior,
    [name]: value,
  }));

  //Cuando esto se ejecute mantendra todos los datos anteriores dl form y solo cambiara aquella c. d texto q el usuario ingresa data
 }

//Vamos hace q se pinte la data cuando la accion se haya realizado
useEffect(() => {
  //Invocamos al action
  obtenerUsuarioActual().then(response => {
    console.log('Esta es la data del objeto response del usuario actual', response);
  })
}, [])



  return (
    <Container component="main" maxWidth="md" justify="center">
      <div style={style.paper}>
        <Typography component="h1" variant="h5">
          Perfil de usuario
        </Typography>
      </div>
      <form style={style.form}>
        <Grid container spacing={2}>
          <Grid item xs={12} md={12}>
            <TextField
              name="NombreCompleto"
              value={usuario.NombreCompleto}
              variant="outlined"
              onChange={ingresarValoresMemoria}
              fullWidth
              label="Ingrese nombre y apellidos"
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              name="Email"
              value={usuario.Email}
              variant="outlined"
              onChange={ingresarValoresMemoria}
              fullWidth
              label="Ingrese email"
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              name="Password"
              
              value={usuario.Password}
              type="password"
              onChange={ingresarValoresMemoria}
              variant="outlined"
              fullWidth
              label="Ingrese password"
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              name="ConfirmarPassword"
              value={usuario.ConfirmarPassword}
              type="password"
              onChange={ingresarValoresMemoria}
              variant="outlined"
              fullWidth
              label="Confirme password"
            />
          </Grid>
        </Grid>

        <Grid container justifyContent="center">
          <Grid item xs={12} md={6}>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              size="large"
              color="primary"
              style={style.submit}
            >
              Guardar Datos
            </Button>
          </Grid>
        </Grid>
      </form>
    </Container>
  );
};

export default PerfilUsuario;
