//'const initialState' es clasico d todos los reducers
//Creamos 2 var, una en false y otra vacia
const initialState = {
  open: false,
  mensaje: "",
};

//Creamos una f q maneje esa data
const openSnackbarReducer = (state = initialState, action) => {
  
  switch (action.type) {
      //Si en caso se llame este reducer "OPEN_SNACKBAR" retorna esos state 'open' y 'mensaje'
    case "OPEN_SNACKBAR":
      return {
        ...state,
        open: action.openMensaje.open,
        mensaje: action.openMensaje.mensaje,
      };
    default:
      return state;
    //else retorna el state actual
  }
};

export default openSnackbarReducer;
