//Imprtamos el arch d HttpCliente
import HttpCliente from "../servicios/HttpCliente";


//Procedemos a crear una f 'registrarUsuario'q me permita registrar usuarios, usando los endPoints d ASP.netCore
//El param 'usuario' tiene toda la data dl usuaio a registrar
//Cuando se habla d procesos d ida y vuelta.. D cliente y servidor SE HABLA DE PROCESOS ASINCRONOS 
//En jScript para manejar el tiempo d espera y retorno d los valores dl server USAMOS LA S PROMESAS, permite aguantar 
// el resultado q el server te envia a futuro 
//Al crear una promesa indicamos q no va terminar esta funcion hasta q el server me haya enviado la respuesta q necesitamos 
export const registrarUsuario = (usuario) => {

  return new Promise((resolve, eject) => {

    //Al momento d ejecutar esta linea se envia el request al servidor o server y en respuesta el server envia una respuesta d tipo ''
    HttpCliente.post('/Usuario/registrar', usuario).then(response=> {

        resolve(response);
    })

  })
}
