//Este archivo 'Perfil' sera un componente d tipo react x eso indicamos q import las librerias d react
import React, { useEffect, useState } from 'react';
import axios from 'axios';

{
  /* 'props' son las propiedades dlcomponente q estan entrando d afuera-- */
  /* 'useEffects' este metodo trabaja cada q se produce un cambio dentro d las propieds q entran al componnt REACT
  por ejemplo en este caso por medio de 'props' en este ejemplo, cada q cambie 'atributomio' cambiara tambien el 
  nombre de la pagina-- 
  --> then retorna un resultado q abre una funcion para imprimir la data
  -->> const [paises esta var d estado va almacenar todos los paises d esa URL
    > Y cambia su valor  cuando invocamos al metodo obtenerPaises y le indicamos q se trata d una 
    variable d estado useState([]) coomo es un array dejamos los []
    > */
}

function Perfil(props) {
  const [paises, obtenerPaises] = useState([]);
  const [status, cambiarStatus] = useState(false);

  useEffect(() => {
    axios.get('https://restcountries.eu/rest/v2/all')

      .then(resultado => {
        obtenerPaises(resultado.data);
        cambiarStatus(true);
      })
      .catch(error => {
        cambiarStatus(true);
      })

  }, [])

  {
    /* retorname los valores d una lista */
  }

  if(status) {
    return (
      <ul>

        {paises.map((pais, indice) => 
          <li key={indice}> {pais.name}</li>
        )}

      </ul>
    );

  }else{
    return(
    <h1> Esta cargando los valores del Rest Service.... </h1>
      );
  }
}
export default Perfil;
