import React, { useState } from "react";
import style from "../Tool/Style";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import {
  Avatar,
  Button,
  Container,
  TextField,
  Typography,
} from "@material-ui/core";
import { loginUsuario } from "../../actions/UsuarioAction";
import { useStateValue } from '../../contexto/store';

const Login = () => {
  //Recordar siempre importar useState, useEffect etc...
  //Creamos nuestras var d estado y components ReactHooks
  const [{usuarioSesion}, dispatch]  = useStateValue();
  const [usuario, setUsuario] = useState({
    Email: '',
    Password: '',
  })

  //Creamos el metodo q me permite capturar los valores dl user... Para mantener los valores anteriores setUsuario((anterior) y solo modificar 
  //el valor d la caja d txt q el usuario esta modificando algo.. Mantiene todo lo anterior '...anterior,' y q se modifique dond unicamnt el user
  //esta escribiendo algo  [name]: value,
  const ingresarValoresMemoria = (e) => {
    const { name, value } = e.target;
    setUsuario(anterior => ({
      ...anterior,
      [name]: value,
    }))
  }

  const loginUsuarioBoton = e =>{
      e.preventDefault();
      loginUsuario(usuario, dispatch).then(response => {
          console.log('response.data.token', response.data.token);
          window.localStorage.setItem('token_seguridad', response.data.token);
          if(response.status === 200) {
            window.localStorage.setItem('token_seguridad', response.data.token);
            props.history.push("/");
        }else{
            dispatch({
                type : "OPEN_SNACKBAR",
                openMensaje : {
                    open : true,
                    mensaje : "Las credenciales del usuario son incorrectas"
                }
            })
        }

        


    })
}

  return (
    <Container maxWidth="xs">
      <div style={style.paper}>
        {/* Dentro d 'avatar' colocaremos el icono */}
        <Avatar style={style.avatar}>
          <LockOutlinedIcon style={style.icon} />
        </Avatar>
        <Typography component="h1" variant="h5">
          Login de Usuario
        </Typography>
        <form style={style.form}>
          <TextField
            name="Email"
            value={usuario.Email}
            onChange={ingresarValoresMemoria}
            variant="outlined"
            label="Ingrese username- email"
            fullWidth
          />
          <TextField
            name="Password"
            value={usuario.Password}
            onChange={ingresarValoresMemoria}
            variant="outlined"
            type="password"
            label="password"
            fullWidth
            margin="normal"
          />
          <Button
            type="submit"
            onClick={loginUsuarioBoton}
            fullWidth
            variant="contained"
            color="primary"
            style={style.submit}
          >
            Enviar
          </Button>
        </form>
      </div>
    </Container>
  );
};

export default Login;
