import React, { createContext, useContext, useReducer } from "react";

//Creamos un obj d tipo contexto
// 'const StateProvider' integra todos los obj componentes hooks dl prjct
//For us to be able to do it, we need to use 'reducer'-- 'initialState' es el storage q almacena las var globales
// 'children' means all react hooks components from us projct

//'StateContext.Provider' Implemnt provider and suscribe all comp with these features for examp the comp index, App.js, Menu.js, etc

export const StateProvider = ({ reducer, initialState, children }) => (
  <StateContext.Provider value={useReducer(reducer, initialState)}>
    {children}
  </StateContext.Provider>
);

// We create this f, With 'const useStateValue' we have acces to all var globals on my context
export const useStateValue = () => useContext(StateContext);
