const style = {
    // marginTop = a un margen superior de 8-- display: "flex" = 1 tras otro
    // flexDirection: "column" = fomato q se deben organizar los obj dentro del div = a 1 detras d otro en est caso
    // alignItems: "center" = alineame los elementos de mi div
    paper: {
      marginTop: 8,
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
    },
    form: {
      //Toda la pantalla y con un margen dsd el titulo hacia abajo de 20
      width: "100%",
      marginTop: 20,
    },
    submit: {
      marginTop: 15
    },
    avatar:{
      margin: 5,
      backgroundColor:"#1976d2",
      width:60,
      height:60
    },
    icon:{
      fontSize: 40
    }
  };

  export default style;
