import { IconButton, Toolbar, Typography, makeStyles } from "@material-ui/core";
import React from "react";

const useStyles = makeStyles((theme) => ({
  //useStyles utiliza una f llamada makeStyles y makeStyles allows invoke a las librerias responsive d materialDesign en este caso el 'theme'
  //el valor q definimos d manera global en el inicio d nuestro projct, lo estamos pasando al 'materialDesign'
  //Arranca en display 'none' cuando reconoza una pantalla 'md' osea tablet o desktop, rompe el none y show su contenido
  seccionDesktop: {
    display: "none",
    [theme.breakpoints.up("md")]: {
      display: "flex",
    },
  },
  seccionMobile: {
    display: "flex",
    [theme.breakpoints.up("md")]: {
      display: "none",
    },
  },
  //Toma todo el espacio d un div, ubica el boton de 3 puntos al final evitando q se pegue el h6 'Cursos Online' y ese btn
  grow: {
    flexGrow: 1,
  },
  avatarSize: {
    width: 40,
    height: 40,
  },
}));

const BarSesion = () => {
  return (
    <Toolbar>
      <IconButton color="inherit">
        <i className="material-icons">menu</i>
      </IconButton>
      <Typography variant="h6">Cursos Online</Typography>
    </Toolbar>
  );
};

export default BarSesion;
