import {
  IconButton,
  Toolbar,
  Typography,
  makeStyles,
  Avatar,
  Button,
} from "@material-ui/core";
import React from "react";
import { useStateValue } from "../../../contexto/store";
import FotoUsuarioTemp from "../../../logo.svg";

const useStyles = makeStyles((theme) => ({
  //useStyles utiliza una f llamada makeStyles y makeStyles allows invoke a las librerias responsive d materialDesign en este caso el 'theme'
  //el valor q definimos d manera global en el inicio d nuestro projct, lo estamos pasando al 'materialDesign'
  //Arranca en display 'none' cuando reconoza una pantalla 'md' osea tablet o desktop, rompe el none y show su contenido
  seccionDesktop: {
    display: "none",
    [theme.breakpoints.up("md")]: {
      display: "flex",
    },
  },
  seccionMobile: {
    display: "flex",
    [theme.breakpoints.up("md")]: {
      display: "none",
    },
  },
  //"grow" Toma todo el espacio d un div, es como 1 bloq q divide dsd el medio, evitando q se pegue el h6 'Cursos Online' y ese btn d la esquina
  grow: {
    flexGrow: 1,
  },
  avatarSize: {
    width: 40,
    height: 40,
  },
}));

const BarSesion = () => {
  const classes = useStyles();
  const [{ sesionUsuario }, dispatch] = useStateValue();
  //iNVOK a mi var global d estado, adicional el obj 'dispatch' verificams q 'useStateValue' se import
  // 'sesionUsuario' esta en contexto/reducers/index.js osea habiamos definid nuestra var d sesion
// 'sesionUsuario ?' si este valor existe pintame el nombre completo else pinta en blanco*/
//const [{ sesionUsuario }, dispatch] = useStateValue(); allow acceder al valor global d la sesion d usuario

  return (
    <Toolbar>
      <IconButton color="inherit">
        <i className="material-icons">menu</i>
      </IconButton>
      <Typography variant="h6">Cursos Online</Typography>
      <div className={classes.grow}></div>

      <div className={classes.seccionDesktop}>
        <Button color="inherit">Salir</Button>
        <Button color="inherit">
          {sesionUsuario ? sesionUsuario.usuario.nombreCompleto :  ""}
        </Button>
        
        <Avatar src={FotoUsuarioTemp}></Avatar>
      </div>
      <div className={classes.seccionMobile}>
        <IconButton color="inherit">
          <i className="material-icons">more_vert</i>
        </IconButton>
      </div>
    </Toolbar>
  );
};

export default BarSesion;
